sqlMap.update:
UPDATE evento.usuario
SET senha=#senha#
WHERE  id=#id#;

sqlMap.select:
SELECT *
FROM evento.usuario
WHERE UPPER(nome) LIKE UPPER(#var#);

select u.id, u.nome, u.senha, u.fone, u.data_nasc,u.email,u.sexo,u.instituicao, u.rua,u.bairro, u.num_casa, u.cidade, u.uf, u.certificado_gerado
from evento.atividade as a, evento.atividade_responsavel as ar, evento.usuario as u
where a.id=ar.atividade_id and u.id=ar.responsavel_id and a.id=#var#;
